INSERT INTO `ktv_songs` VALUES (1, '山水相恋', '朱一龙 / 江疏影', '03:16', '2020-03-13', '山水相恋 - 朱一龙/江疏影<br/>\r\n词：恩山<br/>\r\n曲：孟文豪<br/>\r\n编曲：于韵非<br/>\r\n吉他：亓乐<br/>\r\n和音：王馨悦/杨子轩<br/>\r\n和声编写：成骏<br/>\r\n小提琴：叶霖<br/>\r\n人声录音：栾鹏祥/冯云生<br/>\r\n乐队录音：谢桐<br/>\r\n配唱制作人：谢桐<br/>\r\n混音：顾潇予<br/>\r\n宣传：胡袁<br/>\r\n制作人：孟文豪<br/>\r\n弦乐：亚洲爱乐交响乐团<br/>\r\n录音棚：北京酷音国际STUDIO<br/>\r\n每天想多看一眼<br/>\r\n时刻在绕着你转<br/>\r\n山水之间真情相恋<br/>\r\n上天所赐金玉良缘<br/>\r\n春天山花烂漫<br/>\r\n夏天水流潺潺<br/>\r\n秋天层林尽染<br/>\r\n冬天雪漫山涧<br/>\r\n山水相恋<br/>\r\n一年一年<br/>\r\n爱到永远<br/>\r\n海枯石烂<br/>\r\n山水相恋<br/>\r\n一年一年<br/>\r\n爱到永远<br/>\r\n海枯石烂<br/>\r\n山水相恋<br/>\r\n一年一年<br/>\r\n爱到永远<br/>\r\n海枯石烂<br/>\r\n一生看你不厌倦<br/>\r\n一世都在我心田<br/>\r\n真情相恋山水之间<br/>\r\n深情留住你的容颜<br/>\r\n春风轻拂笑脸<br/>\r\n夏阳照亮山泉<br/>\r\n秋水情意缠绵<br/>\r\n冬日温暖心间<br/>\r\n山水相恋<br/>\r\n一年一年<br/>\r\n爱到永远<br/>\r\n海枯石烂<br/>\r\n山水相恋<br/>\r\n一年一年<br/>\r\n爱到永远<br/>\r\n海枯石烂<br/>\r\n爱到永远<br/>\r\n海枯石烂', 'images/songlist/shanshui.jpg');
INSERT INTO `ktv_songs` VALUES (2, 'How To Be Lonely (Explicit)', 'Rita Ora', '02:55', '2020-03-13', 'How To Be Lonely (Explicit) - Rita Ora<br/>\r\nLyrics by：Lewis Capaldi/Peter Rycroft/Thomas Mann<br/>\r\nComposed by：Lewis Capaldi/Peter Rycroft/Thomas Mann<br/>\r\nHe told me that he loved me more than most<br/>\r\nThat he could be the one to take me home<br/>\r\nBut am I good enough to<br/>\r\nBe the everything that he could want<br/>\r\nHe told me that he loved me more than most<br/>\r\nBeen looking for a non-material love<br/>\r\nSoon as I find it I\'ll be f**king it up<br/>\r\nBreaking up<br/>\r\nLike I ain\'t made a mess here often enough<br/>\r\nNot enough not enough not enough<br/>\r\nNot enough no no no<br/>\r\nWell no one ever showed me how to be lonely<br/>\r\nEnd up on my own almost every night<br/>\r\nI must be the only feeling like losing you showed me how to be lonely<br/>\r\nAin\'t nobody can hold me<br/>\r\nLike the way you hold me<br/>\r\nEnd up on my own almost every night<br/>\r\nI must be the only feeling like losing you showed me how to be lonely<br/>\r\nFeeling like losing you showed me how to be lonely<br/>\r\nBut am I good enough to<br/>\r\nTell you that I love you more than most<br/>\r\nThat you could be the one to take me home<br/>\r\nBut am I good enough to<br/>\r\nGive you everything that you were owed<br/>\r\nIn truth I\'m being honest I don\'t know<br/>\r\nBeen looking for a non-material love<br/>\r\nSoon as I find it I\'ll be f**king it up breaking up<br/>\r\nLike I ain\'t made a mess here often enough<br/>\r\nNot enough not enough not enough<br/>\r\nNot enough no no no<br/>\r\nWell no one ever showed me how to be lonely<br/>\r\nEnd up on my own almost every night<br/>\r\nI must be the only feeling like losing you showed me how to be lonely<br/>\r\nFeeling like losing you showed me how to be lonely<br/>\r\nBut am I good enough to<br/>\r\nOh yeah yeah<br/>\r\nWell no one ever showed me how to be lonely<br/>\r\nEnd up on my own almost every night<br/>\r\nI must be the only feeling like losing you showed me how to be lonely<br/>\r\nAin\'t nobody can hold me<br/>\r\nLike the way you hold me<br/>\r\nEnd up on my own almost every night<br/>\r\nI must be the only feeling like losing you showed me how to be lonely<br/>\r\nAlone<br/>\r\nLonely<br/>\r\nFeeling like losing you showed me how to be lonely<br/>\r\nBe lonely be alone<br/>\r\nFeeling like losing you showed me how to be lonely', 'images/songlist/howto.jpg');
INSERT INTO `ktv_songs` VALUES (3, '夜航星 《我的三体之章北海传》动画片尾', '不才 / 三体宇宙', '05:03', '2020-03-13', '夜航星 - 不才/三体宇宙<br/>\r\n词：黑金雨<br/>\r\n曲：moonik蛛蛛<br/>\r\n制作人/编曲：Yu H.<br/>\r\n主唱录音棚：Studio 21A<br/>\r\n录音师：Yu H.<br/>\r\n伴唱：夏铜子<br/>\r\n鼓：Tom DuPree III<br/>\r\n吉他：Yu H.<br/>\r\n贝斯：Chris Kollias/张小她<br/>\r\n打击乐：Larry Salzman/Yu H.<br/>\r\n弦乐编写：胡静成/Yu H.<br/>\r\n弦乐：国际首席爱乐乐团<br/>\r\n弦乐录音：刘璆@九紫天诚<br/>\r\n混音/母带工程师：Yu H.<br/>\r\n母带室：Studio 21A Room C<br/>\r\n监制：冠亚<br/>\r\n企划：moonik蛛蛛<br/>\r\n执念的鱼<br/>\r\n提着灯闯过远洋的甄选<br/>\r\n继续下潜<br/>\r\n无需誓言<br/>\r\n我的心像自沉的旧母舰<br/>\r\n没入深渊<br/>\r\n永别啊<br/>\r\n我曾凝望<br/>\r\n曾是航向的日出<br/>\r\n末路残烛<br/>\r\n容我吹熄<br/>\r\n藏起火种<br/>\r\n向宇宙远渡<br/>\r\n点燃星<br/>\r\n亲手点燃目光尽头的准星<br/>\r\n让夜降临<br/>\r\n让陨石重启如萤火虫闪烁的飞行<br/>\r\n破灭魂灵<br/>\r\n点燃星<br/>\r\n亲手点燃黑暗森林的火星<br/>\r\n蒙昧初醒<br/>\r\n而我却轻声告别这新生的黎明<br/>\r\n执念的鱼<br/>\r\n孤独着闯过自然的甄选<br/>\r\n文明岸边<br/>\r\n无需誓言<br/>\r\n我的心像重启的旧母舰<br/>\r\n去星云巅<br/>\r\n永别啊<br/>\r\n我曾凝望<br/>\r\n曾是航向的日出<br/>\r\n末路残烛<br/>\r\n容我吹熄<br/>\r\n藏起火种<br/>\r\n向宇宙远渡<br/>\r\n我是星<br/>\r\n利剑开刃寒光锋芒的银星<br/>\r\n绝不消隐<br/>\r\n不回顾永难再折返的故园的光阴<br/>\r\n决意前进<br/>\r\n我是星<br/>\r\n我愿投身前途未卜的群星<br/>\r\n为梦长明<br/>\r\n让希望做我无声永存的墓志铭<br/>\r\n我是星<br/>\r\n利剑开刃寒光锋芒的银星<br/>\r\n绝不消隐<br/>\r\n不回顾永难再折返的故园的光阴<br/>\r\n决意前进<br/>\r\n点燃星<br/>\r\n亲手点燃黑暗森林的火星<br/>\r\n蒙昧初醒<br/>\r\n而我却轻声告别这新生的黎明', 'images/songlist/yehangxing.jpg');
