INSERT INTO `ktv_songs` VALUES (5, '都几岁了 (Live)', 'Ella陈嘉桦', '03:59', '2020-03-12', '都几岁了 (Live) - Ella陈嘉桦<br/>\r\n词：张简君伟<br/>\r\n曲：张简君伟<br/>\r\n编曲：Jerry C<br/>\r\n原唱：陈嘉桦Ella<br/>\r\n谁在心上开了一扇天窗<br/>\r\n就让思绪一整夜着了凉<br/>\r\n谁在耳边说了一句谎话<br/>\r\n就让天真的人被捆绑<br/>\r\n谁在心上筑了一道城墙<br/>\r\n遮住月光也遮蔽了希望<br/>\r\n谁让眼角塌了一座堤防<br/>\r\n泪水泛滥成一片汪洋<br/>\r\n怎么<br/>\r\n都几岁了<br/>\r\n还一样<br/>\r\n长不大<br/>\r\n真的相信<br/>\r\n地会久<br/>\r\n天会长<br/>\r\n感情里面的人来人往<br/>\r\n但真心不是有来有往<br/>\r\n都几岁了<br/>\r\n不应该<br/>\r\n放不下<br/>\r\n这一路上<br/>\r\n有些人<br/>\r\n该遗忘<br/>\r\n幸福不会是红着眼眶<br/>\r\n快乐不该是有点逞强<br/>\r\n何必勉强<br/>\r\n人们渴望能够自由飞翔<br/>\r\n却忘了我们都没有翅膀<br/>\r\n你和我没有什么不一样<br/>\r\n受了伤也只能更坚强<br/>\r\n怎么<br/>\r\n都几岁了<br/>\r\n还一样<br/>\r\n长不大<br/>\r\n真的相信<br/>\r\n地会久<br/>\r\n天会长<br/>\r\n感情里面的人来人往<br/>\r\n但真心不是有来有往<br/>\r\n都几岁了<br/>\r\n不应该<br/>\r\n放不下<br/>\r\n这一路上<br/>\r\n有些人<br/>\r\n该遗忘<br/>\r\n幸福不会是红着眼眶<br/>\r\n快乐不该是有点逞强<br/>\r\n何必勉强<br/>\r\n都几岁了<br/>\r\n还一样<br/>\r\n长不大<br/>\r\n真的相信<br/>\r\n地会长<br/>\r\n天会久<br/>\r\n感情里面的人来人往<br/>\r\n但真心不是有来有往<br/>\r\n都几岁了<br/>\r\n不应该<br/>\r\n放不下<br/>\r\n这一路上<br/>\r\n有些人<br/>\r\n该遗忘<br/>\r\n幸福不会是红着眼眶<br/>\r\n快乐不该是有点逞强<br/>\r\n何必勉强<br/>\r\n何必勉强<br/>\r\n既然心上开了一扇天窗<br/>\r\n不如就好好的晒晒月亮<br/>\r\n既然眼角塌了一座堤防<br/>\r\n不如就好好的哭一场<br/>\r\n又怎样', 'images/songlist/doujisui.jpg');
INSERT INTO `ktv_songs` VALUES (6, 'Born to Die', 'Kelly Clarkson', '03:25', '2020-03-13', 'Born to Die - Kelly Clarkson<br/>\r\nLyrics by：Justin Timberlake/Chris Stapleton<br/>\r\nComposed by：Justin Timberlake/Chris Stapleton<br/>\r\nWe don\'t hope for makin\' things better<br/>\r\nAll is want is to keep it together<br/>\r\nEvery day is a rainy day no changin\' the weather<br/>\r\nThis kind of life has made our hearts as hard as leather<br/>\r\nAnd all these tears are from the dust in our eyes<br/>\r\nAnd all these years just keep on passin\' us by<br/>\r\nAnd all this misery makes it hard to deny<br/>\r\nWe all know that we\'re just born to die<br/>\r\nThat\'s the way it goes when you\'re just born to die<br/>\r\nI hear the voice of a whipper way singin\'<br/>\r\nShe don\'t know all the comfort she\'s bringin\'<br/>\r\nYe all know the sound of when the truth is ringin\'<br/>\r\nYeah it hurts your soul just like a bumblebee stingin\'<br/>\r\nAll these tears are from the dust in our eyes<br/>\r\nAnd all these years just keep on passin\' us by<br/>\r\nAnd all this misery makes it hard to deny<br/>\r\nWe all know that we\'re just born to die<br/>\r\nThat\'s the way it goes when you\'re just born to die<br/>\r\nAll these tears are from the dust in our eyes<br/>\r\nAnd all these years just keep on passin\' us by<br/>\r\nAnd all this misery makes it hard to deny<br/>\r\nWe all know that we\'re just born to die<br/>\r\nWe all know that we\'re just born to die<br/>\r\nThat\'s the way it goes when you\'re just born to die', 'images/songlist/borntodie.jpg');
